nota = float(input("Digite a nota do aluno: "))

if nota &gt;= 9.0:
    conceito = "A"
elif nota &gt;= 7.0:
    conceito = "B"
elif nota &gt;= 5.0:
    conceito = "C"
else:
    conceito = "D"

print(f"Nota: {nota} - Conceito: {conceito}")

